import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBackground from './components/AnimatedBackground';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: white;
  padding: 2rem;
  position: relative;
`;

const Title = styled(motion.h1)`
  font-size: 3.5rem;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, #FFD700, #FFA500);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const AddressContainer = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  padding: 2rem;
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 215, 0, 0.1);
  margin: 2rem 0;
  text-align: center;
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
`;

const BuyButton = styled(motion.button)`
  padding: 1rem 3rem;
  font-size: 1.5rem;
  background: linear-gradient(45deg, #FFD700, #FFA500);
  border: none;
  border-radius: 30px;
  color: #1a1a1a;
  cursor: pointer;
  margin: 2rem 0;
  font-weight: bold;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const SocialButtons = styled.div`
  display: flex;
  gap: 2rem;
  margin: 2rem 0;
`;

const SocialButton = styled(motion.a)`
  font-size: 2.5rem;
  color: white;
  cursor: pointer;
  transition: color 0.3s;

  &:hover {
    color: #FFD700;
  }
`;

const Footer = styled(motion.div)`
  margin-top: auto;
  padding: 2rem;
  font-size: 1.2rem;
  text-align: center;
`;

function App() {
  return (
    <>
      <AnimatedBackground />
      <AppContainer>
        <Title
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          $JIGGIT
        </Title>

        <AddressContainer
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          <h2>Token Address</h2>
          <p>0x123...789</p>
        </AddressContainer>

        <BuyButton
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          Buy $JIGGIT
        </BuyButton>

        <SocialButtons>
          <SocialButton
            as={motion.a}
            href="https://t.me/jiggit"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FaTelegram />
          </SocialButton>
          <SocialButton
            as={motion.a}
            href="https://twitter.com/jiggit"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FaXTwitter />
          </SocialButton>
          <SocialButton
            as={motion.a}
            href="https://medium.com/@jiggit"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FaMedium />
          </SocialButton>
        </SocialButtons>

        <Footer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8 }}
        >
          Join to our Jiggit nomad community
        </Footer>
      </AppContainer>
    </>
  );
}

export default App;
