import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Styled components (reuse from App.js or adjust as needed)
const HeroRow = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1100px;
  gap: 2.7rem;
  margin: 0.5rem 0 2.2rem 0;
  @media (max-width: 900px) {
    flex-direction: column;
    gap: 1.2rem;
    max-width: 98vw;
  }
  @media (max-width: 700px) {
    gap: 0.7rem;
    margin: 0.7rem 0 1.2rem 0;
  }
`;

const MascotCircle = styled(motion.div)`
  width: 320px;
  height: 320px;
  border-radius: 50%;
  overflow: hidden;
  border: 10px solid #e8b03b;
  box-shadow: 0 0 0 24px #f7c87333, 0 20px 80px 0 #5a3b1b44;
  background: linear-gradient(135deg, #c97d3d 70%, #e8b03b 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 2.5rem;
  margin-top: 2.5rem;
  position: relative;
  z-index: 2;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    width: 180px;
    height: 180px;
    border-width: 6px;
    margin-bottom: 1.5rem;
    margin-top: 1.5rem;
  }
  @media (max-width: 700px) {
    width: 120px;
    height: 120px;
    border-width: 4px;
    margin-bottom: 0.7rem;
    margin-top: 0.7rem;
  }
`;

const HeroTextCol = styled.div`
  align-items: flex-start;
  max-width: 520px;
  width: 100%;
  font-size: 0.95rem;
  padding: 1.3rem 1.2rem;
  @media (max-width: 900px) {
    align-items: center;
    text-align: center;
    font-size: 0.85rem;
    max-width: 98vw;
  }
  @media (max-width: 700px) {
    font-size: 0.75rem;
    padding: 0.7rem 0.5rem;
  }
`;

const Title = styled(motion.h1)`
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  font-size: 1.7rem;
  font-weight: 900;
  color: #c97d3d;
  margin-bottom: 1.2rem;
  letter-spacing: 2.5px;
  text-shadow: 2px 2px 0 #e8b03b, 0 0 8px #c97d3d;
  text-align: center;
  text-transform: uppercase;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 1.1rem;
    margin-bottom: 0.7rem;
  }
`;

const TextCard = styled.div`
  background: #f7eee1;
  border: 2px solid #c97d3d;
  border-radius: 0.7rem;
  box-shadow: 0 2px 8px 0 #c97d3d11;
  padding: 1.1rem 1.2rem;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  color: #5a3b1b;
  font-size: 0.92rem;
  margin-bottom: 1.1rem;
  margin-top: 0.5rem;
  width: 100%;
  text-align: left;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.8rem;
    padding: 0.8rem 0.7rem;
    margin-bottom: 0.7rem;
  }
`;

const Description = styled(motion.div)`
  font-size: 1.1rem;
  color: #5a3b1b;
  margin-bottom: 1.5rem;
  text-align: left;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  line-height: 1.4;
  letter-spacing: 0.01em;
  white-space: pre-line;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.85rem;
    margin-bottom: 0.7rem;
    text-align: center;
  }
`;

const BuyButton = styled(motion.button)`
  padding: 1.1rem 2.1rem;
  font-size: 1.05rem;
  background: #f7eee1;
  border: 2px solid #c97d3d;
  border-radius: 0.7rem;
  color: #5a3b1b;
  cursor: pointer;
  font-weight: 900;
  margin: 1.3rem 0 0.7rem 0;
  box-shadow: 0 2px 8px 0 #c97d3d11;
  text-shadow: none;
  letter-spacing: 1px;
  transition: transform 0.2s, box-shadow 0.2s, background 0.2s, color 0.2s;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  image-rendering: pixelated;
  &:hover {
    transform: scale(1.07);
    box-shadow: 0 4px 24px 0 #f7c87318;
    background: #e8b03b;
    color: #c97d3d;
    border-color: #e8b03b;
  }
  @media (max-width: 700px) {
    font-size: 0.93rem;
    padding: 0.8rem 1.2rem;
  }
`;

const HeroSection = () => (
  <HeroRow>
    <MascotCircle
      initial={{ scale: 0 }}
      animate={{ scale: 1 }}
      transition={{ type: 'spring', stiffness: 180, damping: 18 }}
    >
      <img src="/assets/mascot.png" alt="Jiggit Mascot" style={{ width: '100%', height: '100%', objectFit: 'contain', imageRendering: 'pixelated' }} />
    </MascotCircle>
    <HeroTextCol>
      <Title>$JIGGIT</Title>
      <TextCard>
        <b>JIGGIT â€” THE SPIRIT OF CENTRAL ASIA ON THE BLOCKCHAIN</b>
        <br /><br />
        JIGGIT is a cryptocurrency designed to spark global interest in Central Asia and power a new economy for tourism, culture, and local communities.
      </TextCard>
      <BuyButton
        whileHover={{ scale: 1.07 }}
        whileTap={{ scale: 0.98 }}
      >
        BUY $JIGGIT
      </BuyButton>
    </HeroTextCol>
  </HeroRow>
);

export default HeroSection;
