import React from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";
import { motion } from "framer-motion";
import { FaTelegram, FaMedium } from "react-icons/fa";
import { FaXTwitter } from "react-icons/fa6";
import AnimatedBg from "./components/AnimatedBg";
import HeroSection from "./components/HeroSection";
import Roadmap from "./components/RoadmapSection";
import TokenAddressComponent, {
  NavTokenAddress,
} from "./components/TokenAddressContainer";

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = "#c97d3d";
const richBrown = "#5a3b1b";
const goldYellow = "#f7c873";
const deepYellow = "#e8b03b";
const lightBrown = "#a67c52";
const pixelDark = "#23190f";
const pixelLight = "#f7eee1";

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Enhanced mobile responsiveness for Navbar
const Navbar = styled.nav`
  width: 98vw;
  max-width: 98vw;
  margin: 0.5rem auto;
  background: #fff1b8;
  border-radius: 1.7rem;
  border: 4px solid #181818;
  box-shadow: 2px 6px 0 0 #181818;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.4rem 1.5rem;
  min-height: 3.7rem;
  gap: 1.1rem;
  font-family: ${pixelFont};
  position: sticky;
  top: 0.5rem;
  z-index: 1000;
  flex-wrap: wrap;

  @media (max-width: 900px) {
    width: 95vw;
    max-width: 95vw;
    padding: 0.3rem 1rem;
    gap: 0.8rem;
  }

  @media (max-width: 600px) {
    width: 94vw;
    max-width: 94vw;
    padding: 0.3rem 0.5rem;
    gap: 0.4rem;
    border-width: 3px;
    border-radius: 1.2rem;
  }
`;

const NavbarToken = styled.div`
  font-family: ${pixelFont};
  font-size: 1.6rem;
  font-weight: 900;
  color: #181818;
  white-space: nowrap;

  @media (max-width: 900px) {
    font-size: 1.4rem;
    order: 1;
  }

  @media (max-width: 600px) {
    font-size: 1.2rem;
    width: 100%;
    text-align: center;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap: 0.8rem;

  @media (max-width: 900px) {
    order: 2;
    justify-content: center;
    width: 100%;
    gap: 0.6rem;
    margin: 0.3rem 0;
  }
`;

const NavbarLinkButton = styled.a`
  background: #fff1b8;
  color: #181818;
  border: 2.5px solid #181818;
  border-radius: 1.3rem;
  padding: 0.4rem 1.15rem;
  font-family: ${pixelFont};
  font-size: 1rem;
  font-weight: 900;
  box-shadow: 1.5px 2.5px 0 0 #181818;
  text-decoration: none;
  transition: all 0.17s ease;
  cursor: pointer;
  white-space: nowrap;
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: fit-content;

  @media (max-width: 900px) {
    font-size: 0.9rem;
    padding: 0.35rem 0.9rem;
    border-width: 2px;
  }

  @media (max-width: 600px) {
    font-size: 0.8rem;
    padding: 0.3rem 0.7rem;
    border-width: 2px;
    box-shadow: 1px 2px 0 0 #181818;
  }

  &:hover {
    background: ${accent};
    color: #fff1b8;
    border-color: ${accent};
    transform: translateY(-1px);
    box-shadow: 0 4px 12px ${accent}66;
  }
`;

const NavbarIcons = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  @media (max-width: 900px) {
    order: 3;
    gap: 0.8rem;
  }

  @media (max-width: 600px) {
    width: 100%;
    justify-content: center;
    gap: 0.6rem;
    margin: 0.2rem 0;
  }
`;

const NavbarIcon = styled.a`
  color: #181818;
  font-size: 1.25rem;
  opacity: 0.96;
  text-decoration: none;
  transition: color 0.17s, opacity 0.17s;
  &:hover {
    color: ${accent};
    opacity: 1;
  }
`;

const NavbarBuyButton = styled.a`
  background: linear-gradient(135deg, #ffd700, #daa520);
  color: #181818;
  border: 2.5px solid #8b4513;
  border-radius: 1.3rem;
  padding: 0.4rem 1.15rem;
  font-family: ${pixelFont};
  font-size: 1rem;
  font-weight: 900;
  box-shadow: 1.5px 2.5px 0 0 #8b4513, inset 0 0 5px rgba(139, 69, 19, 0.5);
  text-decoration: none;
  margin-left: 0.6rem;
  transition: all 0.17s ease;
  cursor: pointer;
  white-space: nowrap;
  overflow: visible;
  text-overflow: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  letter-spacing: 0.5px;

  @media (max-width: 900px) {
    font-size: 0.85rem;
    padding: 0.3rem 0.9rem;
