import React, { useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';

// RPG background brown/gold theme
const gradientMove = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: -1;
  overflow: hidden;
  pointer-events: none;
`;

const GradientOverlay = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  background: linear-gradient(120deg, #3e2a13 0%, #6e4b23 40%, #ffd700 100%);
  opacity: 0.32;
  background-size: 200% 200%;
  animation: ${gradientMove} 18s ease-in-out infinite;
  z-index: 0;
`;

const Canvas = styled.canvas`
  position: absolute;
  width: 100vw;
  height: 100vh;
  z-index: 1;
`;

const AnimatedBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let particles = [];

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    class Particle {
      constructor() {
        this.reset();
      }

      reset() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 3 + 1;
        this.speedX = Math.random() * 1.2 - 0.6;
        this.speedY = Math.random() * 1.2 - 0.6;
        this.opacity = Math.random() * 0.5 + 0.2;
        // RPG gold sparkle
        this.color = `rgba(255, 215, 80, ${this.opacity})`;
        this.glow = Math.random() > 0.7;
      }

      update() {
        this.x += this.speedX;
        this.y += this.speedY;
        if (this.x > canvas.width || this.x < 0) this.speedX *= -1;
        if (this.y > canvas.height || this.y < 0) this.speedY *= -1;
      }

      draw() {
        ctx.save();
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.shadowColor = this.glow ? 'rgba(255, 215, 80, 0.7)' : 'rgba(110,75,35,0.12)';
        ctx.shadowBlur = this.glow ? 18 : 7;
        ctx.fillStyle = this.color;
        ctx.fill();
        ctx.restore();
      }
    }

    const init = () => {
      resizeCanvas();
      particles = [];
      for (let i = 0; i < 48; i++) {
        particles.push(new Particle());
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });

      // Draw connecting lines (subtle gold lines)
      particles.forEach((particle, index) => {
        for (let j = index + 1; j < particles.length; j++) {
          const dx = particle.x - particles[j].x;
          const dy = particle.y - particles[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          if (distance < 100) {
            ctx.save();
            ctx.beginPath();
            ctx.strokeStyle = `rgba(255, 215, 80, ${0.05 * (1 - distance/100)})`;
            ctx.lineWidth = 1;
            ctx.moveTo(particle.x, particle.y);
            ctx.lineTo(particles[j].x, particles[j].y);
            ctx.shadowColor = 'rgba(255, 215, 80, 0.25)';
            ctx.shadowBlur = 4;
            ctx.stroke();
            ctx.restore();
          }
        }
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    init();
    animate();
    window.addEventListener('resize', init);
    return () => {
      window.removeEventListener('resize', init);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return (
    <BackgroundContainer>
      <GradientOverlay />
      <Canvas ref={canvasRef} />
    </BackgroundContainer>
  );
};

export default AnimatedBackground;
