import React from 'react';
import styled from 'styled-components';

const pixelFont = "'Press Start 2P', 'Spectral SC', monospace";

const RoadmapSection = styled.section`
  margin: 2.2rem 0 1.5rem 0;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 2;
`;

const RoadmapList = styled.ul`
  display: flex;
  flex-direction: row;
  justify-content: center;
  gap: 1.5rem;
  padding: 0;
  margin: 0;
  list-style: none;
  @media (max-width: 700px) {
    flex-direction: column;
    gap: 0.8rem;
  }
`;

const RoadmapField = styled.div`
  background: #fff1b8;
  border: 2.5px solid #181818;
  border-radius: 1.3rem;
  padding: 0.4rem 1.15rem;
  font-family: ${pixelFont};
  font-size: 1rem;
  font-weight: 900;
  box-shadow: 1.5px 2.5px 0 0 #181818;
  margin: 0.5rem 0;
  display: flex;
  align-items: center;
  justify-content: center;
  white-space: nowrap;
  min-width: fit-content;

  @media (max-width: 900px) {
    font-size: 0.9rem;
    padding: 0.35rem 0.9rem;
    border-width: 2px;
  }

  @media (max-width: 600px) {
    font-size: 0.8rem;
    padding: 0.3rem 0.7rem;
    border-width: 2px;
  }
`;

const RoadmapTitle = styled.h2`
  font-family: ${pixelFont};
  color: #c97d3d;
  font-size: 1.6rem;
  margin-bottom: 1.3rem;
  font-weight: 900;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  text-align: center;
  image-rendering: pixelated;
  text-shadow: 2px 2px 0 #f7eee1, 0 0 10px #c97d3d33;
  @media (max-width: 700px) {
    font-size: 1.1rem;
    margin-bottom: 0.8rem;
  }
`;

const Roadmap = () => (
  <RoadmapSection>
    <RoadmapTitle>ROADMAP</RoadmapTitle>
    <RoadmapList>
      <RoadmapField>Phase 1: Launch & Community</RoadmapField>
      <RoadmapField>Phase 2: Game Integration</RoadmapField>
      <RoadmapField>Phase 3: Marketplace & Rewards</RoadmapField>
    </RoadmapList>
  </RoadmapSection>
);

export default Roadmap;
