import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBg from './components/AnimatedBg';
import HeroSection from './components/HeroSection';
import Roadmap from './components/RoadmapSection';

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = '#c97d3d';
const richBrown = '#5a3b1b';
const goldYellow = '#f7c873';
const deepYellow = '#e8b03b';
const lightBrown = '#a67c52';
const pixelDark = '#23190f';
const pixelLight = '#f7eee1';

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Navbar - horizontal, pixel art style
const Navbar = styled.nav`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 1.7rem 3vw 1.1rem 2vw;
  box-sizing: border-box;
  border-bottom: 6px solid ${accent};
  position: sticky;
  top: 0;
  z-index: 10;
  font-family: ${pixelFont};
  image-rendering: pixelated;
  @media (max-width: 700px) {
    flex-direction: column;
    gap: 1rem;
    padding: 1rem;
  }
`;
const NavbarBrand = styled.div`
  font-family: ${pixelFont};
  font-size: 2.1rem;
  color: ${accent};
  font-weight: 900;
  text-shadow: 2px 2px 0 ${richBrown}, 0 0 8px ${accent};
  letter-spacing: 3px;
  cursor: pointer;
  text-transform: uppercase;
  margin-bottom: 0;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 1.3rem;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2.1rem;
  align-items: center;
  @media (max-width: 700px) {
    gap: 0.7rem;
  }
`;

const NavbarLink = styled.a`
  color: ${accent};
  font-size: 1.1rem;
  text-decoration: none;
  padding: 0.62rem 2.1rem;
  border-radius: 9px;
  background: ${richBrown};
  border: 3px solid ${accent};
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background 0.2s, color 0.2s, box-shadow 0.2s;
  font-family: ${pixelFont};
  letter-spacing: 0.04em;
  image-rendering: pixelated;
  box-shadow: 3px 3px 0 ${secondary};
  &:hover {
    background: ${accent};
    color: ${richBrown};
    box-shadow: 6px 6px 0 ${primary};
  }
  @media (max-width: 700px) {
    font-size: 0.9rem;
    padding: 0.4rem 1.1rem;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  color: ${textMain};
  padding: 3.2rem 1.5rem 2rem 1.5rem;
  position: relative;
  font-family: ${pixelFont};
  background: transparent;
  gap: 2.5rem;
  z-index: 1;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    gap: 1.5rem;
    padding: 1.2rem 0.4rem;
  }
  @media (max-width: 700px) {
    gap: 0.7rem;
    padding: 0.6rem 0.2rem 0.6rem 0.2rem;
  }
`;

function App() {
  return (
    <>
      <AnimatedBg />
      <Navbar>
        <NavbarBrand>JIGGIT</NavbarBrand>
        <NavbarLinks>
          <NavbarLink href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer"><FaTelegram />Telegram</NavbarLink>
          <NavbarLink href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer"><FaXTwitter />Twitter</NavbarLink>
          <NavbarLink href="https://medium.com/@jiggit" target="_blank" rel="noopener noreferrer"><FaMedium />Medium</NavbarLink>
        </NavbarLinks>
      </Navbar>
      <AppContainer>
        <HeroSection />
        <Roadmap />
      </AppContainer>
    </>
  );
}

export default App;
