import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = '#c97d3d';
const richBrown = '#5a3b1b';
const goldYellow = '#f7c873';
const deepYellow = '#e8b03b';
const lightBrown = '#a67c52';
const pixelDark = '#23190f';
const pixelLight = '#f7eee1';

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Animated background (vertical gradient, subtle pixel shimmer)
const AnimatedBg = styled.div`
  position: fixed;
  top: 0; left: 0; width: 100vw; height: 100vh;
  z-index: 0;
  background: linear-gradient(180deg, ${background} 0%, ${richBrown} 100%);
  &:after {
    content: '';
    position: absolute;
    top: 0; left: 0; width: 100vw; height: 100vh;
    pointer-events: none;
    background: repeating-linear-gradient(135deg, ${deepYellow}11 0 4px, transparent 4px 16px, ${lightBrown}11 16px 20px, transparent 20px 32px);
    opacity: 0.25;
    animation: pixelAnim 13s linear infinite;
  }
  @keyframes pixelAnim {
    0% { background-position: 0 0; }
    100% { background-position: 128px 128px; }
  }
`;

// Navbar - horizontal, dark orange
const Navbar = styled.nav`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 1.1rem 2vw 1.1rem 2vw;
  box-sizing: border-box;
  border-bottom: 4px solid ${accent};
  position: sticky;
  top: 0;
  z-index: 10;
  font-family: ${pixelFont};
  image-rendering: pixelated;
  @media (max-width: 700px) {
    flex-direction: column;
    gap: 0.7rem;
    padding: 0.7rem 1vw;
  }
`;

const NavbarBrand = styled.div`
  font-family: ${pixelFont};
  font-size: 1.2rem;
  color: ${accent};
  font-weight: 900;
  text-shadow: 2px 2px 0 ${richBrown}, 0 0 8px ${accent};
  letter-spacing: 2px;
  cursor: pointer;
  text-transform: uppercase;
  margin-bottom: 0;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 1rem;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  flex-direction: row;
  gap: 1.1rem;
  align-items: center;
  @media (max-width: 700px) {
    gap: 0.5rem;
  }
`;

const NavbarLink = styled.a`
  color: ${accent};
  font-size: 0.8rem;
  text-decoration: none;
  padding: 0.38rem 1.1rem;
  border-radius: 7px;
  background: ${richBrown};
  border: 2px solid ${accent};
  display: flex;
  align-items: center;
  gap: 0.3rem;
  transition: background 0.2s, color 0.2s, box-shadow 0.2s;
  font-family: ${pixelFont};
  letter-spacing: 0.03em;
  image-rendering: pixelated;
  box-shadow: 2px 2px 0 ${secondary};
  &:hover {
    background: ${accent};
    color: ${richBrown};
    box-shadow: 4px 4px 0 ${primary};
  }
  @media (max-width: 700px) {
    font-size: 0.7rem;
    padding: 0.25rem 0.7rem;
  }
`;

// AppContainer - vertical, centered
const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  color: ${textMain};
  padding: 2rem 0.8rem 1.2rem 0.8rem;
  position: relative;
  font-family: ${pixelFont};
  background: transparent;
  gap: 1.2rem;
  z-index: 1;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    gap: 0.7rem;
    padding: 0.6rem 0.2rem 0.6rem 0.2rem;
  }
`;

// CardColumn - vertical layout
const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.1rem;
  width: 100%;
  max-width: 340px;
  align-items: center;
  justify-content: flex-start;
  image-rendering: pixelated;
`;

// Card - vertical, brown border, soft pixel shadow
const Card = styled(motion.div)`
  background: ${cardBg};
  border-radius: 14px;
  box-shadow: 0 0 0 3px ${cardBorder}, 0 4px 16px 0 ${richBrown}22;
  padding: 1.1rem 0.8rem;
  max-width: 340px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 3px solid ${cardBorder};
  position: relative;
  font-family: ${pixelFont};
  box-sizing: border-box;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    padding: 0.7rem 0.2rem;
    max-width: 96vw;
    min-width: 0;
  }
`;

// Mascot/photo area - much bigger and fully rounded
const MascotCircle = styled(motion.div)`
  width: 320px;
  height: 320px;
  border-radius: 50%;
  overflow: hidden;
  border: 10px solid ${accent};
  box-shadow: 0 0 0 24px ${secondary}33, 0 20px 80px 0 ${richBrown}44;
  background: linear-gradient(135deg, ${primary} 70%, ${accent} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 2.5rem;
  margin-top: 2.5rem;
  position: relative;
  z-index: 2;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    width: 180px;
    height: 180px;
    border-width: 6px;
    margin-bottom: 1.5rem;
    margin-top: 1.5rem;
  }
`;

const MascotImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
  image-rendering: pixelated;
`;

const Title = styled(motion.h1)`
  font-family: ${pixelFont};
  font-size: 1.1rem;
  font-weight: 900;
  color: ${primary};
  margin-bottom: 0.7rem;
  letter-spacing: 1.5px;
  text-shadow: 2px 2px 0 ${accent}, 0 0 8px ${primary};
  text-align: center;
  text-transform: uppercase;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
  }
`;

const Description = styled(motion.div)`
  font-size: 0.9rem;
  color: ${textMain};
  margin-bottom: 0.9rem;
  text-align: left;
  font-family: ${pixelFont};
  line-height: 1.3;
  letter-spacing: 0.01em;
  white-space: pre-line;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.75rem;
    margin-bottom: 0.6rem;
    text-align: left;
  }
`;

const QualitiesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 0.5rem 0;
  text-align: left;
`;

const Quality = styled.li`
  font-size: 0.9rem;
  color: ${primary};
  margin: 0.18rem 0;
  padding-left: 1.1em;
  position: relative;
  font-family: ${pixelFont};
  &:before {
    content: 'â—†';
    color: ${secondary};
    position: absolute;
    left: 0;
    font-size: 1em;
    top: 0.02em;
    image-rendering: pixelated;
  }
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.7rem;
  }
`;

const AddressText = styled.div`
  font-family: ${pixelFont};
  font-size: 0.9rem;
  background: linear-gradient(90deg, ${cardBorder} 70%, ${cardBg} 100%);
  color: ${primary};
  padding: 0.6rem 0.8rem;
  border-radius: 0.6rem;
  margin-top: 0.4rem;
  border: 2px solid ${accent};
  word-break: break-all;
  box-shadow: 0 2px 8px 0 ${primary}11;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.7rem;
    padding: 0.4rem 0.4rem;
  }
`;

const BuyButton = styled(motion.button)`
  padding: 0.8rem 1.5rem;
  font-size: 0.92rem;
  background: linear-gradient(90deg, ${primary} 0%, ${accent} 100%);
  border: 2.5px solid ${primary};
  border-radius: 0.6rem;
  color: ${accent};
  cursor: pointer;
  font-weight: 900;
  margin: 1rem 0 0.5rem 0;
  box-shadow: 0 2px 12px 0 ${primary}13;
  text-shadow: 2px 2px 0 ${accent};
  letter-spacing: 1px;
  transition: transform 0.2s, box-shadow 0.2s, background 0.2s;
  font-family: ${pixelFont};
  image-rendering: pixelated;
  &:hover {
    transform: scale(1.07);
    box-shadow: 0 4px 24px 0 ${secondary}18;
    background: linear-gradient(90deg, ${accent} 0%, ${primary} 100%);
    color: ${primary};
  }
  @media (max-width: 700px) {
    font-size: 0.7rem;
    padding: 0.5rem 0.7rem;
  }
`;

const Footer = styled(Card)`
  font-size: 0.9rem;
  color: ${primary};
  background: linear-gradient(90deg, ${cardBg} 80%, ${cardBorder} 100%);
  margin-bottom: 1.2rem;
  border: 2px solid ${primary};
  font-family: ${pixelFont};
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 0.7rem;
    margin-bottom: 0.7rem;
  }
`;

// Roadmap Section
const RoadmapSection = styled.section`
  width: 100vw;
  max-width: 100vw;
  margin: 0 auto 0.7rem auto;
  padding: 0.5rem 0 2.4rem 0;
  overflow-x: auto;
`;
const RoadmapTitle = styled.h2`
  font-family: ${pixelFont};
  color: ${primary};
  font-size: 1.3rem;
  margin: 0 0 1.1rem 1.7vw;
  font-weight: 900;
  letter-spacing: 1px;
  text-shadow: 2px 2px 0 ${accent}, 0 0 8px ${primary};
  text-transform: uppercase;
  image-rendering: pixelated;
`;
const RoadmapScroller = styled.div`
  display: flex;
  flex-direction: row;
  gap: 1.3rem;
  overflow-x: auto;
  padding: 0.2rem 1vw 0.2rem 1.7vw;
  scroll-snap-type: x mandatory;
  @media (max-width: 600px) {
    gap: 0.7rem;
    padding: 0.2rem 0.5vw 0.2rem 0.7vw;
  }
`;
const RoadmapCard = styled.div`
  min-width: 260px;
  background: ${cardBg};
  border: 4px solid ${primary};
  border-radius: 0;
  box-shadow: 0 0 0 4px ${cardBorder};
  color: ${textMain};
  font-family: ${pixelFont};
  font-size: 0.9rem;
  padding: 1.1rem 1.1rem;
  scroll-snap-align: start;
  display: flex;
  flex-direction: column;
  gap: 0.6rem;
  image-rendering: pixelated;
  @media (max-width: 600px) {
    min-width: 160px;
    font-size: 0.7rem;
    padding: 0.7rem 0.5rem;
  }
`;

function App() {
  return (
    <>
      <AnimatedBg />
      <Navbar>
        <NavbarBrand>JIGGIT</NavbarBrand>
        <NavbarLinks>
          <NavbarLink href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer"><FaTelegram />Telegram</NavbarLink>
          <NavbarLink href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer"><FaXTwitter />Twitter</NavbarLink>
          <NavbarLink href="https://medium.com/@jiggit" target="_blank" rel="noopener noreferrer"><FaMedium />Medium</NavbarLink>
        </NavbarLinks>
      </Navbar>
      <AppContainer>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <MascotCircle
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ type: 'spring', stiffness: 180, damping: 18 }}
            >
              <MascotImg src="/assets/mascot.png" alt="Jiggit Mascot" />
            </MascotCircle>
            <Title>$JIGGIT</Title>
            <Description>
              <b>JIGGIT â€” THE SPIRIT OF CENTRAL ASIA ON THE BLOCKCHAIN</b>
              <br /><br />
              JIGGIT is not just a cryptocurrency â€” itâ€™s the heartbeat of Central Asia, reimagined for the digital age.<br /><br />
              <span style={{ display: 'block', marginBottom: '0.7em' }}>
                Our mission is to revive global interest in the rich heritage, landscapes, and spirit of Central Asia, while building a new economy for tourism, culture, and adventure.
              </span>
              With JIGGIT, travelers can access unique experiences, earn rewards for exploration, and support local businesses and communities directly and transparently.<br /><br />
              Inspired by the legendary <b>"JIGITS"</b> â€” the fearless, agile horsemen who symbolized honor and freedom â€” JIGGIT brings that same spirit into the future: <b>BOLD, UNSTOPPABLE, AUTHENTIC</b>.
            </Description>
            <QualitiesList>
              <Quality>brave</Quality>
              <Quality>agile</Quality>
              <Quality>courageous</Quality>
              <Quality>honest</Quality>
              <Quality>fair</Quality>
              <Quality>able not only to fight but also to protect the honor of his family or people.</Quality>
            </QualitiesList>
          </Card>
          <Footer
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.1, delay: 0.25 }}
          >
            JOIN TO OUR JIGGIT NOMAD COMMUNITY
          </Footer>
        </CardColumn>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.9, delay: 0.1 }}
          >
            <Title>TOKEN ADDRESS</Title>
            <AddressText>0x123...789</AddressText>
            <BuyButton
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.98 }}
            >
              BUY $JIGGIT
            </BuyButton>
          </Card>
        </CardColumn>
      </AppContainer>
      <RoadmapSection>
        <RoadmapTitle>ROADMAP</RoadmapTitle>
        <RoadmapScroller>
          <RoadmapCard>
            <b>Q2 2025: LAUNCH & COMMUNITY</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Token launch & DEX listing</li>
              <li>Community building</li>
              <li>Initial partnerships with local businesses</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q3 2025: ECOSYSTEM GROWTH</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Traveler rewards system</li>
              <li>Tourism experience marketplace</li>
              <li>Mobile app beta</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q4 2025: GLOBAL EXPANSION</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Major exchange listings</li>
              <li>International marketing</li>
              <li>Full launch of JIGGIT platform</li>
            </ul>
          </RoadmapCard>
        </RoadmapScroller>
      </RoadmapSection>
    </>
  );
}

export default App;
