import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBg from './components/AnimatedBg';
import HeroSection from './components/HeroSection';
import Roadmap from './components/RoadmapSection';

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = '#c97d3d';
const richBrown = '#5a3b1b';
const goldYellow = '#f7c873';
const deepYellow = '#e8b03b';
const lightBrown = '#a67c52';
const pixelDark = '#23190f';
const pixelLight = '#f7eee1';

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Redesign Navbar to match the new screenshot
const Navbar = styled.nav`
  width: 98vw;
  max-width: 98vw;
  margin: 0 auto 0 auto;
  background: #fff1b8;
  border-radius: 1.7rem;
  border: 4px solid #181818;
  box-shadow: 2px 6px 0 0 #181818;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.4rem 1.5rem 0.4rem 1.1rem;
  min-height: 3.7rem;
  gap: 1.1rem;
  font-family: ${pixelFont};
  image-rendering: pixelated;
  position: relative;
  z-index: 20;
  @media (max-width: 700px) {
    flex-direction: column;
    border-radius: 1rem;
    padding: 0.3rem 0.7rem;
    min-height: 2.2rem;
    gap: 0.5rem;
  }
`;

const NavbarToken = styled.div`
  font-family: ${pixelFont};
  font-size: 1.6rem;
  font-weight: 900;
  color: #181818;
  letter-spacing: 1.5px;
  margin-right: 1.1rem;
  @media (max-width: 700px) {
    font-size: 1.1rem;
    margin-right: 0.5rem;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 1.1rem;
  margin-left: 1.2rem;
  @media (max-width: 700px) {
    gap: 0.5rem;
    margin-left: 0.3rem;
  }
`;

const NavbarLinkButton = styled.a`
  background: #fff1b8;
  color: #181818;
  border: 2.5px solid #181818;
  border-radius: 1.3rem;
  padding: 0.4rem 1.15rem;
  font-family: ${pixelFont};
  font-size: 1rem;
  font-weight: 900;
  box-shadow: 1.5px 2.5px 0 0 #181818;
  text-decoration: none;
  transition: background 0.17s, color 0.17s, border 0.17s, box-shadow 0.17s;
  cursor: pointer;
  &:hover {
    background: ${accent};
    color: #fff1b8;
    border: 2.5px solid ${accent};
    box-shadow: 0 6px 20px ${accent}66;
  }
  @media (max-width: 700px) {
    font-size: 0.85rem;
    padding: 0.28rem 0.7rem;
  }
`;

const NavbarIcons = styled.div`
  display: flex;
  align-items: center;
  gap: 1.2rem;
  margin-left: auto;
  margin-right: 1.2rem;
  @media (max-width: 700px) {
    gap: 0.6rem;
    margin-right: 0.3rem;
  }
`;

const NavbarIcon = styled.a`
  color: #181818;
  font-size: 1.25rem;
  opacity: 0.96;
  text-decoration: none;
  transition: color 0.17s, opacity 0.17s;
  &:hover {
    color: ${accent};
    opacity: 1;
  }
`;

const NavbarBuyButton = styled.a`
  background: #fff1b8;
  color: #181818;
  border: 3px solid #181818;
  border-radius: 1.4rem;
  padding: 0.5rem 1.5rem;
  font-family: ${pixelFont};
  font-size: 1.1rem;
  font-weight: 900;
  box-shadow: 1.5px 2.5px 0 0 #181818;
  text-decoration: none;
  margin-left: 0.6rem;
  transition: background 0.17s, color 0.17s, border 0.17s, box-shadow 0.17s;
  cursor: pointer;
  &:hover {
    background: ${accent};
    color: #fff1b8;
    border: 3px solid ${accent};
    box-shadow: 0 6px 20px ${accent}66;
  }
  @media (max-width: 700px) {
    font-size: 0.9rem;
    padding: 0.35rem 0.9rem;
    margin-left: 0.2rem;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  color: ${textMain};
  padding: 0.2rem 1.5rem 2rem 1.5rem;
  position: relative;
  font-family: ${pixelFont};
  background: transparent;
  gap: 2.5rem;
  z-index: 1;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    gap: 1.5rem;
    padding: 1.5rem; /* Увеличено с 1.2rem 0.4rem */
  }
  @media (max-width: 700px) {
    gap: 1rem; /* Увеличено с 0.7rem */
    padding: 1rem; /* Увеличено с 0.6rem 0.2rem для отступов */
  }
`;

const MascotBottom = styled.img`
  display: block;
  margin: 2rem auto 0 auto;
  width: 210px;
  height: auto;
  image-rendering: pixelated;
  filter: drop-shadow(0 8px 32px #c97d3d44);
  @media (max-width: 700px) {
    width: 120px;
    margin-top: 1.2rem;
  }
`;

const BottomSection = styled.section`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding: 0 0 2.5rem 0;
  margin-top: 0;
  border-radius: 0 0 1.5rem 1.5rem;
  min-height: 320px;
  position: relative;
  z-index: 11;
  overflow: hidden;
  @media (max-width: 700px) {
    border-radius: 0 0 0.7rem 0.7rem;
    min-height: 180px;
  }
`;

const BottomBuyButton = styled.a`
  display: block;
  margin: 2.2rem auto 0 auto;
  background: #f7eee1;
  color: ${primary};
  font-family: ${pixelFont};
  font-size: 2rem;
  font-weight: bold;
  border: 4px solid ${richBrown};
  border-radius: 2.5rem;
  padding: 1.2rem 3.5rem;
  box-shadow: 0 4px 0 0 ${richBrown};
  text-align: center;
  text-decoration: none;
  transition: background 0.18s, color 0.18s, border 0.18s, box-shadow 0.18s;
  cursor: pointer;
  &:hover {
    background: ${accent};
    color: #fff8e1;
    border: 4px solid ${accent};
    box-shadow: 0 8px 24px ${accent}66;
  }
  @media (max-width: 700px) {
    font-size: 1.1rem;
    padding: 0.7rem 1.5rem;
  }
`;

const BottomSubtext = styled.div`
  color: ${richBrown};
  font-family: ${pixelFont};
  font-size: 1rem;
  margin: 1.5rem auto 0 auto;
  opacity: 0.7;
  text-align: center;
  @media (max-width: 700px) {
    font-size: 0.8rem;
    margin: 0.8rem auto 0 auto;
  }
`;

const BottomMascot = styled.img`
  display: block;
  margin: 2.5rem auto 0 auto;
  width: 210px;
  height: auto;
  image-rendering: pixelated;
  filter: drop-shadow(0 8px 32px #c97d3d44);
  border-radius: 1.2rem 1.2rem 2.5rem 2.5rem;
  @media (max-width: 700px) {
    width: 120px;
    margin-top: 1.2rem;
  }
`;

function App() {
  return (
    <>
      <AnimatedBg />
      <Navbar>
        <NavbarToken>$JIGGIT</NavbarToken>
        <NavbarLinks>
          <NavbarLinkButton href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer">Telegram</NavbarLinkButton>
          <NavbarLinkButton href="https://medium.com/@zerobladeamir" target="_blank" rel="noopener noreferrer">Medium</NavbarLinkButton>
          <NavbarLinkButton href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer">X</NavbarLinkButton>
        </NavbarLinks>
