import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';

// Color palette based on user-provided colors and tutorialtoken.com style
const mainRed = '#C25C4F';
const darkRed = '#B62C31';
const darkBlue = '#292C34';
const teal = '#5C9689';
const paleGreen = '#A7BE8F';
const white = '#fff';
const black = '#181A20';
const borderGray = '#23242a';

// Navbar
const Navbar = styled.nav`
  width: 100vw;
  background: linear-gradient(90deg, ${darkBlue} 85%, ${darkRed} 100%);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.1rem 2.7vw 1.1rem 1.7vw;
  box-sizing: border-box;
  border-bottom: 2.5px solid ${mainRed};
  position: sticky;
  top: 0;
  z-index: 10;
  @media (max-width: 600px) {
    flex-direction: column;
    gap: 0.7rem;
    padding: 0.7rem 1vw;
  }
`;

const NavbarBrand = styled.div`
  font-family: 'Spectral SC', serif;
  font-size: 2.1rem;
  color: ${mainRed};
  font-weight: 700;
  text-shadow: 0 2px 0 ${darkBlue}, 0 0 8px ${mainRed};
  letter-spacing: 2.1px;
  cursor: pointer;
  text-transform: uppercase;
  letter-spacing: 0.12em;
`;

const NavbarLinks = styled.div`
  display: flex;
  gap: 1.3rem;
  align-items: center;
`;

const NavbarLink = styled.a`
  color: ${mainRed};
  font-size: 1.18rem;
  text-decoration: none;
  padding: 0.42rem 1.1rem;
  border-radius: 14px;
  background: ${darkBlue};
  border: 1.5px solid ${mainRed};
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background 0.2s, color 0.2s;
  font-family: 'Spectral SC', serif;
  letter-spacing: 0.04em;
  &:hover {
    background: ${mainRed};
    color: ${white};
  }
  @media (max-width: 600px) {
    font-size: 1rem;
    padding: 0.3rem 0.7rem;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: center;
  color: ${white};
  padding: 2.5rem 1.5rem;
  position: relative;
  font-family: 'Spectral SC', serif;
  background: ${black};
  gap: 2.5rem;
  @media (max-width: 900px) {
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    padding: 1.2rem 0.4rem;
  }
`;

const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  width: 100%;
  max-width: 540px;
  @media (max-width: 900px) {
    max-width: 100vw;
    width: 100vw;
    align-items: center;
    justify-content: center;
  }
`;

const Card = styled(motion.div)`
  background: linear-gradient(135deg, ${darkBlue} 82%, ${black} 100%);
  border-radius: 16px;
  box-shadow: 0 8px 32px 0 rgba(44, 44, 52, 0.30), 0 2px 8px 0 rgba(44,44,52,0.18);
  padding: 2.1rem 1.6rem;
  max-width: 480px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 2.5px solid ${mainRed};
  position: relative;
  font-family: 'Spectral SC', serif;
  box-sizing: border-box;
  @media (max-width: 600px) {
    padding: 1.1rem 0.4rem;
    max-width: 98vw;
    min-width: 0;
  }
`;

const MascotCircle = styled(motion.div)`
  width: 110px;
  height: 110px;
  border-radius: 50%;
  overflow: hidden;
  border: 3.5px solid ${teal};
  box-shadow: 0 0 0 7px rgba(92,150,137,0.15), 0 6px 24px 0 rgba(44, 44, 52, 0.20);
  background: linear-gradient(135deg, ${darkBlue} 70%, ${teal} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.1rem;
  position: relative;
  z-index: 2;
  @media (max-width: 600px) {
    width: 72px;
    height: 72px;
    margin-bottom: 0.6rem;
  }
`;

const MascotImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Title = styled(motion.h1)`
  font-family: 'Spectral SC', serif;
  font-size: 2.2rem;
  font-weight: 700;
  color: ${mainRed};
  margin-bottom: 0.8rem;
  letter-spacing: 1.8px;
  text-shadow: 0 2px 0 ${darkBlue}, 0 0 8px ${mainRed};
  text-align: center;
  text-transform: uppercase;
  @media (max-width: 600px) {
    font-size: 1.3rem;
    margin-bottom: 0.5rem;
  }
`;

const Description = styled(motion.div)`
  font-size: 1.08rem;
  color: ${white};
  margin-bottom: 1.1rem;
  text-align: left;
  font-family: 'Spectral SC', serif;
  line-height: 1.6;
  letter-spacing: 0.01em;
  white-space: pre-line;
  @media (max-width: 600px) {
    font-size: 0.93rem;
    margin-bottom: 0.7rem;
    text-align: left;
  }
`;

const QualitiesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 0.5rem 0;
  text-align: left;
`;

const Quality = styled.li`
  font-size: 1.01rem;
  color: ${paleGreen};
  margin: 0.22rem 0;
  padding-left: 1.3em;
  position: relative;
  font-family: 'Spectral SC', serif;
  &:before {
    content: '‚ü°';
    color: ${teal};
    position: absolute;
    left: 0;
    font-size: 1.07em;
    top: 0.02em;
  }
  @media (max-width: 600px) {
    font-size: 0.83rem;
  }
`;

const AddressText = styled.div`
  font-family: 'Spectral SC', serif;
  font-size: 1.05rem;
  background: linear-gradient(90deg, ${darkBlue} 70%, ${black} 100%);
  color: ${mainRed};
  padding: 0.8rem 1.1rem;
  border-radius: 8px;
  margin-top: 0.5rem;
  border: 2px solid ${teal};
  word-break: break-all;
  box-shadow: 0 2px 8px 0 rgba(92,150,137,0.11);
  @media (max-width: 600px) {
    font-size: 0.87rem;
    padding: 0.6rem 0.6rem;
  }
`;

const BuyButton = styled(motion.button)`
  padding: 1rem 2.3rem;
  font-size: 1.08rem;
  background: linear-gradient(90deg, ${mainRed} 0%, ${teal} 100%);
  border: 2.5px solid ${mainRed};
  border-radius: 16px;
  color: ${white};
  cursor: pointer;
  font-weight: 700;
  margin: 1.3rem 0 0.7rem 0;
  box-shadow: 0 2px 12px 0 rgba(92,150,137,0.13);
  text-shadow: 0 1px 0 ${mainRed};
  letter-spacing: 1px;
  transition: transform 0.2s, box-shadow 0.2s, background 0.2s;
  font-family: 'Spectral SC', serif;
  &:hover {
    transform: scale(1.07);
    box-shadow: 0 4px 24px 0 rgba(92,150,137, 0.18);
    background: linear-gradient(90deg, ${teal} 0%, ${mainRed} 100%);
    color: ${mainRed};
  }
  @media (max-width: 600px) {
    font-size: 0.92rem;
    padding: 0.8rem 1.2rem;
  }
`;

const Footer = styled(Card)`
  font-size: 1.03rem;
  color: ${mainRed};
  background: linear-gradient(90deg, ${darkBlue} 80%, ${black} 100%);
  margin-bottom: 2rem;
  border: 2px solid ${mainRed};
  @media (max-width: 600px) {
    font-size: 0.85rem;
    margin-bottom: 1rem;
  }
`;

// Roadmap Section
const RoadmapSection = styled.section`
  width: 100vw;
  max-width: 100vw;
  margin: 0 auto 0.7rem auto;
  padding: 0.5rem 0 2.4rem 0;
  overflow-x: auto;
`;
const RoadmapTitle = styled.h2`
  font-family: 'Spectral SC', serif;
  color: ${mainRed};
  font-size: 1.45rem;
  margin: 0 0 1.1rem 1.7vw;
  font-weight: 700;
  letter-spacing: 1px;
  text-shadow: 0 2px 0 ${darkBlue}, 0 0 8px ${mainRed};
  text-transform: uppercase;
`;
const RoadmapScroller = styled.div`
  display: flex;
  flex-direction: row;
  gap: 1.3rem;
  overflow-x: auto;
  padding: 0.2rem 1vw 0.2rem 1.7vw;
  scroll-snap-type: x mandatory;
  @media (max-width: 600px) {
    gap: 0.7rem;
    padding: 0.2rem 0.5vw 0.2rem 0.7vw;
  }
`;
const RoadmapCard = styled.div`
  min-width: 260px;
  background: linear-gradient(135deg, ${darkBlue} 88%, ${black} 100%);
  border: 2px solid ${teal};
  border-radius: 10px;
  box-shadow: 0 4px 16px 0 rgba(44, 44, 52, 0.13);
  color: ${white};
  font-family: 'Spectral SC', serif;
  font-size: 0.98rem;
  padding: 1.1rem 1.1rem;
  scroll-snap-align: start;
  display: flex;
  flex-direction: column;
  gap: 0.6rem;
  @media (max-width: 600px) {
    min-width: 160px;
    font-size: 0.83rem;
    padding: 0.7rem 0.5rem;
  }
`;

function App() {
  return (
    <>
      <Navbar>
        <NavbarBrand>JIGGIT</NavbarBrand>
        <NavbarLinks>
          <NavbarLink href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer"><FaTelegram />Telegram</NavbarLink>
          <NavbarLink href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer"><FaXTwitter />Twitter</NavbarLink>
          <NavbarLink href="https://medium.com/@jiggit" target="_blank" rel="noopener noreferrer"><FaMedium />Medium</NavbarLink>
        </NavbarLinks>
      </Navbar>
      <AppContainer>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <MascotCircle
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ type: 'spring', stiffness: 180, damping: 18 }}
            >
              <MascotImg src="/assets/mascot.png" alt="Jiggit Mascot" />
            </MascotCircle>
            <Title>$JIGGIT</Title>
            <Description>
              <b>JIGGIT ‚Äî THE SPIRIT OF CENTRAL ASIA ON THE BLOCKCHAIN</b>
              <br /><br />
              JIGGIT is not just a cryptocurrency ‚Äî it‚Äôs the heartbeat of Central Asia, reimagined for the digital age.<br /><br />
              <span style={{ display: 'block', marginBottom: '0.7em' }}>
                Our mission is to revive global interest in the rich heritage, landscapes, and spirit of Central Asia, while building a new economy for tourism, culture, and adventure.
              </span>
              With JIGGIT, travelers can access unique experiences, earn rewards for exploration, and support local businesses and communities directly and transparently.<br /><br />
              Inspired by the legendary <b>"JIGITS"</b> ‚Äî the fearless, agile horsemen who symbolized honor and freedom ‚Äî JIGGIT brings that same spirit into the future: <b>BOLD, UNSTOPPABLE, AUTHENTIC</b>.
            </Description>
            <QualitiesList>
              <Quality>brave</Quality>
              <Quality>agile</Quality>
              <Quality>courageous</Quality>
              <Quality>honest</Quality>
              <Quality>fair</Quality>
              <Quality>able not only to fight but also to protect the honor of his family or people.</Quality>
            </QualitiesList>
          </Card>
          <Footer
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.1, delay: 0.25 }}
          >
            JOIN TO OUR JIGGIT NOMAD COMMUNITY
          </Footer>
        </CardColumn>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.9, delay: 0.1 }}
          >
            <Title>TOKEN ADDRESS</Title>
            <AddressText>0x123...789</AddressText>
            <BuyButton
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.98 }}
            >
              BUY $JIGGIT
            </BuyButton>
          </Card>
        </CardColumn>
      </AppContainer>
      <RoadmapSection>
        <RoadmapTitle>ROADMAP</RoadmapTitle>
        <RoadmapScroller>
          <RoadmapCard>
            <b>Q2 2025: LAUNCH & COMMUNITY</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Token launch & DEX listing</li>
              <li>Community building</li>
              <li>Initial partnerships with local businesses</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q3 2025: ECOSYSTEM GROWTH</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Traveler rewards system</li>
              <li>Tourism experience marketplace</li>
              <li>Mobile app beta</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q4 2025: GLOBAL EXPANSION</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Major exchange listings</li>
              <li>International marketing</li>
              <li>Full launch of JIGGIT platform</li>
            </ul>
          </RoadmapCard>
        </RoadmapScroller>
      </RoadmapSection>
    </>
  );
}

export default App;
