import React from 'react';
import styled from 'styled-components';

const RoadmapSection = styled.section`
  margin: 2.2rem 0 1.5rem 0;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 2;
`;

const RoadmapList = styled.ul`
  display: flex;
  flex-direction: row;
  justify-content: center;
  gap: 1.5rem;
  padding: 0;
  margin: 0;
  list-style: none;
  @media (max-width: 700px) {
    flex-direction: column;
    gap: 0.8rem;
  }
`;

const RoadmapItem = styled.li`
  background: #f7eee1;
  border: 2px solid #c97d3d;
  border-radius: 0.7rem;
  box-shadow: 0 2px 8px 0 #c97d3d11;
  padding: 1.1rem 1.2rem;
  min-width: 120px;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  color: #5a3b1b;
  font-size: 0.85rem;
  text-align: center;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    min-width: 0;
    font-size: 0.75rem;
    padding: 0.8rem 0.7rem;
  }
`;

const Roadmap = () => (
  <RoadmapSection>
    <h2>ROADMAP</h2>
    <RoadmapList>
      <RoadmapItem>Phase 1: Launch & Community</RoadmapItem>
      <RoadmapItem>Phase 2: Game Integration</RoadmapItem>
      <RoadmapItem>Phase 3: Marketplace & Rewards</RoadmapItem>
    </RoadmapList>
  </RoadmapSection>
);

export default Roadmap;
