import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBackground from './components/AnimatedBackground';

// RPG color palette
const brown = '#6e4b23';
const darkBrown = '#3e2a13';
const parchment = '#f8ecd1';
const gold = '#ffd700';
const yellow = '#ffe066';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: ${darkBrown};
  padding: 2rem;
  position: relative;
  font-family: 'UnifrakturCook', cursive;
  background: none;
`;

const Card = styled(motion.div)`
  background: linear-gradient(135deg, ${parchment} 85%, ${yellow} 100%);
  border-radius: 24px;
  box-shadow: 0 8px 32px 0 rgba(110, 75, 35, 0.14), 0 2px 8px 0 rgba(110,75,35,0.12);
  padding: 2.2rem 1.7rem;
  margin: 1.5rem 0;
  max-width: 440px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 3.5px solid ${gold};
  position: relative;
`;

const RowCard = styled(Card)`
  flex-direction: row;
  gap: 2rem;
  justify-content: center;
  padding: 2rem 2.5rem;
`;

const MascotCircle = styled(motion.div)`
  width: 130px;
  height: 130px;
  border-radius: 50%;
  overflow: hidden;
  border: 6px solid ${gold};
  box-shadow: 0 0 0 8px rgba(110,75,35,0.12), 0 6px 24px 0 rgba(110, 75, 35, 0.17);
  background: linear-gradient(135deg, ${parchment} 80%, ${gold} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.2rem;
  position: relative;
  z-index: 2;
`;

const MascotImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Title = styled(motion.h1)`
  font-family: 'UnifrakturCook', cursive;
  font-size: 2.7rem;
  font-weight: 700;
  color: ${brown};
  margin-bottom: 0.7rem;
  letter-spacing: 2px;
  text-shadow: 0 2px 0 ${gold}, 0 0 6px ${yellow};
  text-align: center;
`;

const Description = styled(motion.p)`
  font-size: 1.15rem;
  color: ${darkBrown};
  margin-bottom: 1.2rem;
  text-align: center;
  font-family: 'Inter', serif;
`;

const QualitiesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 0.5rem 0;
  text-align: left;
`;

const Quality = styled.li`
  font-size: 1.08rem;
  color: ${brown};
  margin: 0.25rem 0;
  padding-left: 1.3em;
  position: relative;
  font-family: 'Inter', serif;
  &:before {
    content: '★';
    color: ${gold};
    position: absolute;
    left: 0;
    font-size: 1.1em;
    top: 0.02em;
  }
`;

const AddressText = styled.div`
  font-family: 'Inter', serif;
  font-size: 1.12rem;
  background: linear-gradient(90deg, #f3e3bb 70%, #ffe066 100%);
  color: ${darkBrown};
  padding: 0.8rem 1.2rem;
  border-radius: 10px;
  margin-top: 0.5rem;
  border: 2px solid ${brown};
  word-break: break-all;
  box-shadow: 0 2px 8px 0 rgba(110,75,35,0.07);
`;

const BuyButton = styled(motion.button)`
  padding: 1rem 2.5rem;
  font-size: 1.18rem;
  background: linear-gradient(90deg, ${gold} 0%, ${brown} 100%);
  border: 2.5px solid ${darkBrown};
  border-radius: 22px;
  color: #fffbe6;
  cursor: pointer;
  font-weight: 700;
  margin: 1.3rem 0 0.7rem 0;
  box-shadow: 0 2px 12px 0 rgba(110,75,35,0.11);
  text-shadow: 0 1px 0 ${brown};
  letter-spacing: 1px;
  transition: transform 0.2s, box-shadow 0.2s, background 0.2s;
  font-family: 'UnifrakturCook', cursive;
  &:hover {
    transform: scale(1.07);
    box-shadow: 0 4px 24px 0 rgba(255, 215, 0, 0.25);
    background: linear-gradient(90deg, ${brown} 0%, ${gold} 100%);
  }
`;

const SocialButtons = styled.div`
  display: flex;
  gap: 1.5rem;
  margin: 1.2rem 0 0.2rem 0;
  justify-content: center;
`;

const SocialButton = styled(motion.a)`
  font-size: 2.1rem;
  color: ${brown};
  background: linear-gradient(120deg, #fffbe6 60%, #ffe066 100%);
  border-radius: 50%;
  padding: 0.5rem;
  border: 2px solid ${gold};
  transition: color 0.2s, background 0.2s, box-shadow 0.2s;
  box-shadow: 0 1.5px 6px 0 rgba(110,75,35,0.13);
  &:hover {
    color: #fffbe6;
    background: ${brown};
    box-shadow: 0 4px 16px 0 rgba(255, 215, 0, 0.13);
  }
`;

const Footer = styled(Card)`
  font-size: 1.12rem;
  color: ${brown};
  background: linear-gradient(90deg, #f8ecd1 80%, #ffe066 100%);
  margin-bottom: 2rem;
  border: 2px solid ${gold};
`;

function App() {
  return (
    <>
      <AnimatedBackground />
      <AppContainer>
        <Card
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <MascotCircle
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: 'spring', stiffness: 180, damping: 18 }}
          >
            <MascotImg src="/assets/mascot.png" alt="Jiggit Mascot" />
          </MascotCircle>
          <Title>$JIGGIT</Title>
          <Description>
            Who are <b>"Dzhigits"</b>?<br /><br />
            In the cultures of Central Asia and the Caucasus (especially among Kazakhs, Kyrgyz, Chechens, and Dagestanis), a dzhigit is a skilled horseman and warrior.<br /><br />
            In Kazakh and Kyrgyz traditions, a dzhigit is not just someone who rides a horse well. He represents the ideal man:
          </Description>
          <QualitiesList>
            <Quality>brave</Quality>
            <Quality>agile</Quality>
            <Quality>courageous</Quality>
            <Quality>honest</Quality>
            <Quality>fair</Quality>
            <Quality>able not only to fight but also to protect the honor of his family or people.</Quality>
          </QualitiesList>
        </Card>

        <Card
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.9, delay: 0.1 }}
        >
          <Title>Token Address</Title>
          <AddressText>0x123...789</AddressText>
          <BuyButton
            whileHover={{ scale: 1.07 }}
            whileTap={{ scale: 0.98 }}
          >
            Buy $JIGGIT
          </BuyButton>
        </Card>

        <Card
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.9, delay: 0.2 }}
        >
          <Title>Join Us</Title>
          <SocialButtons>
            <SocialButton
              href="https://t.me/jiggit"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.15 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaTelegram />
            </SocialButton>
            <SocialButton
              href="https://twitter.com/jiggit"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.15 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaXTwitter />
            </SocialButton>
            <SocialButton
              href="https://medium.com/@jiggit"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.15 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaMedium />
            </SocialButton>
          </SocialButtons>
        </Card>

        <Footer
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.1, delay: 0.25 }}
        >
          Join to our Jiggit nomad community
        </Footer>
      </AppContainer>
    </>
  );
}

export default App;
