import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Enhanced Hero Section with improved visibility and responsiveness
const HeroRow = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1100px;
  gap: 2.7rem;
  margin: 2rem auto;
  padding: 1rem;
  position: relative;
  z-index: 5;

  @media (max-width: 900px) {
    flex-direction: column;
    gap: 1.2rem;
    max-width: 98vw;
    margin: 1rem auto;
  }

  @media (max-width: 600px) {
    gap: 0.7rem;
    margin: 0.5rem auto;
    padding: 0.5rem;
  }
`;

const MascotCircle = styled(motion.div)`
  width: 320px;
  height: 320px;
  border-radius: 50%;
  overflow: hidden;
  border: 10px solid #e8b03b;
  box-shadow: 0 0 0 24px #f7c87333, 0 20px 80px 0 #5a3b1b44;
  background: linear-gradient(135deg, #c97d3d 70%, #e8b03b 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 2rem auto;
  position: relative;
  z-index: 2;
  image-rendering: pixelated;

  @media (max-width: 900px) {
    width: 220px;
    height: 220px;
    border-width: 8px;
    margin: 1.5rem auto;
  }

  @media (max-width: 600px) {
    width: 180px;
    height: 180px;
    border-width: 6px;
    margin: 1rem auto;
  }
`;

const HeroTextCol = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  max-width: 520px;
  width: 100%;
  padding: 1.5rem;
  position: relative;
  z-index: 3;

  @media (max-width: 900px) {
    max-width: 90vw;
    padding: 1rem;
  }

  @media (max-width: 600px) {
    padding: 0.5rem;
  }
`;

const Title = styled(motion.h1)`
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  font-size: 2.2rem;
  font-weight: 900;
  color: #c97d3d;
  margin: 0 0 1.5rem 0;
  letter-spacing: 2.5px;
  text-shadow: 2px 2px 0 #e8b03b, 0 0 8px #c97d3d;
  text-align: center;
  text-transform: uppercase;
  image-rendering: pixelated;

  @media (max-width: 900px) {
    font-size: 1.8rem;
    margin-bottom: 1.2rem;
  }

  @media (max-width: 600px) {
    font-size: 1.4rem;
    margin-bottom: 1rem;
    letter-spacing: 1.5px;
  }
`;

const TextCard = styled.div`
  background: #f7eee1;
  border: 2px solid #c97d3d;
  border-radius: 0.7rem;
  box-shadow: 0 4px 12px 0 #c97d3d22;
  padding: 1.5rem;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  color: #5a3b1b;
  font-size: 1rem;
  margin: 1rem 0;
  width: 100%;
  text-align: center;
  image-rendering: pixelated;

  @media (max-width: 900px) {
    font-size: 0.9rem;
    padding: 1.2rem;
  }

  @media (max-width: 600px) {
    font-size: 0.8rem;
    padding: 1rem;
    margin: 0.7rem 0;
  }
`;

const BuyButton = styled(motion.button)`
  padding: 1.2rem 2.5rem;
  font-size: 1.2rem;
  background: #f7eee1;
  border: 3px solid #c97d3d;
  border-radius: 0.8rem;
  color: #5a3b1b;
  cursor: pointer;
  font-weight: 900;
  margin: 1.5rem 0;
  box-shadow: 0 4px 16px 0 #c97d3d22;
  text-shadow: none;
  letter-spacing: 1px;
  transition: all 0.2s ease;
  font-family: 'Press Start 2P', 'Spectral SC', monospace;
  image-rendering: pixelated;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 24px 0 #c97d3d33;
    background: #e8b03b;
    color: #5a3b1b;
    border-color: #c97d3d;
  }

  @media (max-width: 900px) {
    font-size: 1.1rem;
    padding: 1rem 2rem;
    margin: 1.2rem 0;
  }

  @media (max-width: 600px) {
    font-size: 0.9rem;
    padding: 0.8rem 1.5rem;
    margin: 1rem 0;
    border-width: 2px;
  }
`;

const HeroSection = () => (
  <HeroRow>
    <MascotCircle
      initial={{ scale: 0.8, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ type: 'spring', stiffness: 180, damping: 20 }}
    >
      <img 
        src="/assets/mascot.png" 
        alt="Jiggit Mascot" 
        style={{ 
          width: '90%', 
          height: '90%', 
          objectFit: 'contain', 
          imageRendering: 'pixelated' 
        }} 
      />
    </MascotCircle>
    <HeroTextCol>
      <Title
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.2, duration: 0.5 }}
      >
        $JIGGIT
      </Title>
      <TextCard>
        <b>JIGGIT â€” THE SPIRIT OF CENTRAL ASIA ON THE BLOCKCHAIN</b>
        <br /><br />
        JIGGIT is a cryptocurrency designed to spark global interest in Central Asia and power a new economy for tourism, culture, and local communities.
      </TextCard>
      <BuyButton
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.98 }}
      >
        Buy $JIGGIT
      </BuyButton>
    </HeroTextCol>
  </HeroRow>
);

export default HeroSection;
