import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = '#c97d3d';
const richBrown = '#5a3b1b';
const goldYellow = '#f7c873';
const deepYellow = '#e8b03b';
const lightBrown = '#a67c52';
const pixelDark = '#23190f';
const pixelLight = '#f7eee1';

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Animated background (vertical gradient, subtle pixel shimmer)
const AnimatedBg = styled.div`
  position: fixed;
  top: 0; left: 0; width: 100vw; height: 100vh;
  z-index: 0;
  background: linear-gradient(180deg, ${background} 0%, ${richBrown} 100%);
  &:after {
    content: '';
    position: absolute;
    top: 0; left: 0; width: 100vw; height: 100vh;
    pointer-events: none;
    background: repeating-linear-gradient(135deg, ${deepYellow}11 0 4px, transparent 4px 16px, ${lightBrown}11 16px 20px, transparent 20px 32px);
    opacity: 0.25;
    animation: pixelAnim 13s linear infinite;
  }
  @keyframes pixelAnim {
    0% { background-position: 0 0; }
    100% { background-position: 128px 128px; }
  }
`;

// Navbar - horizontal, pixel art style
const Navbar = styled.nav`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 1.7rem 3vw 1.1rem 2vw;
  box-sizing: border-box;
  border-bottom: 6px solid ${accent};
  position: sticky;
  top: 0;
  z-index: 10;
  font-family: ${pixelFont};
  image-rendering: pixelated;
  @media (max-width: 700px) {
    flex-direction: column;
    gap: 0.7rem;
    padding: 0.7rem 1vw;
  }
`;

const NavbarBrand = styled.div`
  font-family: ${pixelFont};
  font-size: 2.1rem;
  color: ${accent};
  font-weight: 900;
  text-shadow: 2px 2px 0 ${richBrown}, 0 0 8px ${accent};
  letter-spacing: 3px;
  cursor: pointer;
  text-transform: uppercase;
  margin-bottom: 0;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 1.3rem;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2.1rem;
  align-items: center;
  @media (max-width: 700px) {
    gap: 0.7rem;
  }
`;

const NavbarLink = styled.a`
  color: ${accent};
  font-size: 1.1rem;
  text-decoration: none;
  padding: 0.62rem 2.1rem;
  border-radius: 9px;
  background: ${richBrown};
  border: 3px solid ${accent};
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background 0.2s, color 0.2s, box-shadow 0.2s;
  font-family: ${pixelFont};
  letter-spacing: 0.04em;
  image-rendering: pixelated;
  box-shadow: 3px 3px 0 ${secondary};
  &:hover {
    background: ${accent};
    color: ${richBrown};
    box-shadow: 6px 6px 0 ${primary};
  }
  @media (max-width: 700px) {
    font-size: 0.9rem;
    padding: 0.4rem 1.1rem;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  color: ${textMain};
  padding: 3.2rem 1.5rem 2rem 1.5rem;
  position: relative;
  font-family: ${pixelFont};
  background: transparent;
  gap: 2.5rem;
  z-index: 1;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    gap: 1.5rem;
    padding: 1.2rem 0.4rem;
  }
  @media (max-width: 700px) {
    gap: 0.7rem;
    padding: 0.6rem 0.2rem 0.6rem 0.2rem;
  }
`;

const HeroRow = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1100px;
  gap: 2.7rem;
  margin: 1.7rem 0 2.2rem 0;
  @media (max-width: 900px) {
    flex-direction: column;
    gap: 1.2rem;
    max-width: 98vw;
  }
  @media (max-width: 700px) {
    gap: 0.7rem;
    margin: 0.7rem 0 1.2rem 0;
  }
`;

const MascotCircle = styled(motion.div)`
  width: 320px;
  height: 320px;
  border-radius: 50%;
  overflow: hidden;
  border: 10px solid ${accent};
  box-shadow: 0 0 0 24px ${secondary}33, 0 20px 80px 0 ${richBrown}44;
  background: linear-gradient(135deg, ${primary} 70%, ${accent} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 2.5rem;
  margin-top: 2.5rem;
  position: relative;
