import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBackground from './components/AnimatedBackground';

// Dark RPG color palette
const brown = '#3e2a13';
const darkBrown = '#1a1208';
const parchment = '#2b2112';
const gold = '#ffd700';
const yellow = '#ffe066';

// Navbar
const Navbar = styled.nav`
  width: 100vw;
  background: linear-gradient(90deg, #2b2112 80%, #3e2a13 100%);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.1rem 2.7vw 1.1rem 1.7vw;
  box-sizing: border-box;
  border-bottom: 2px solid ${gold};
  position: sticky;
  top: 0;
  z-index: 10;
  @media (max-width: 600px) {
    flex-direction: column;
    gap: 0.7rem;
    padding: 0.7rem 1vw;
  }
`;

const NavbarBrand = styled.div`
  font-family: 'Merriweather', serif;
  font-size: 2.1rem;
  color: ${gold};
  font-weight: 700;
  text-shadow: 0 2px 0 #1a1208, 0 0 8px ${gold};
  letter-spacing: 1.2px;
  cursor: pointer;
`;

const NavbarLinks = styled.div`
  display: flex;
  gap: 1.3rem;
  align-items: center;
`;

const NavbarLink = styled.a`
  color: ${gold};
  font-size: 1.35rem;
  text-decoration: none;
  padding: 0.5rem 0.9rem;
  border-radius: 18px;
  background: linear-gradient(120deg, #2b2112 60%, #3e2a13 100%);
  border: 1.5px solid ${gold};
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background 0.2s, color 0.2s;
  &:hover {
    background: ${gold};
    color: #2b2112;
  }
  @media (max-width: 600px) {
    font-size: 1.1rem;
    padding: 0.3rem 0.7rem;
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: center;
  color: #f8ecd1;
  padding: 2.5rem 1.5rem;
  position: relative;
  font-family: 'Merriweather', serif;
  background: none;
  gap: 2.5rem;
  @media (max-width: 900px) {
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    padding: 1.2rem 0.4rem;
  }
`;

const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  width: 100%;
  max-width: 540px;
  @media (max-width: 900px) {
    max-width: 100vw;
    width: 100vw;
    align-items: center;
    justify-content: center;
  }
`;

const Card = styled(motion.div)`
  background: linear-gradient(135deg, ${parchment} 85%, #3e2a13 100%);
  border-radius: 22px;
  box-shadow: 0 8px 32px 0 rgba(26, 18, 8, 0.24), 0 2px 8px 0 rgba(110,75,35,0.18);
  padding: 2.2rem 1.7rem;
  max-width: 480px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border: 2.5px solid ${gold};
  position: relative;
  font-family: 'Merriweather', serif;
  box-sizing: border-box;
  @media (max-width: 600px) {
    padding: 1.2rem 0.5rem;
    max-width: 98vw;
    min-width: 0;
  }
`;

const MascotCircle = styled(motion.div)`
  width: 140px;
  height: 140px;
  border-radius: 50%;
  overflow: hidden;
  border: 5px solid ${gold};
  box-shadow: 0 0 0 8px rgba(110,75,35,0.16), 0 6px 24px 0 rgba(26, 18, 8, 0.25);
  background: linear-gradient(135deg, #2b2112 70%, ${gold} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.2rem;
  position: relative;
  z-index: 2;
  @media (max-width: 600px) {
    width: 94px;
    height: 94px;
    margin-bottom: 0.7rem;
  }
`;

const MascotImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Title = styled(motion.h1)`
  font-family: 'Merriweather', serif;
  font-size: 2.4rem;
  font-weight: 700;
  color: ${gold};
  margin-bottom: 0.9rem;
  letter-spacing: 1.5px;
  text-shadow: 0 2px 0 #1a1208, 0 0 8px ${gold};
  text-align: center;
  @media (max-width: 600px) {
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
  }
`;

const Description = styled(motion.div)`
  font-size: 1.22rem;
  color: #f8ecd1;
  margin-bottom: 1.2rem;
  text-align: left;
  font-family: 'Merriweather', serif;
  line-height: 1.7;
  letter-spacing: 0.01em;
  white-space: pre-line;
  @media (max-width: 600px) {
    font-size: 1rem;
    margin-bottom: 0.7rem;
    text-align: left;
  }
`;

const QualitiesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 0.5rem 0;
  text-align: left;
`;

const Quality = styled.li`
  font-size: 1.16rem;
  color: ${gold};
  margin: 0.25rem 0;
  padding-left: 1.3em;
  position: relative;
  font-family: 'Merriweather', serif;
  &:before {
    content: '✦';
    color: ${yellow};
    position: absolute;
    left: 0;
    font-size: 1.1em;
    top: 0.02em;
  }
  @media (max-width: 600px) {
    font-size: 0.95rem;
  }
`;

const AddressText = styled.div`
  font-family: 'Merriweather', serif;
  font-size: 1.18rem;
  background: linear-gradient(90deg, #2b2112 70%, #3e2a13 100%);
  color: ${gold};
  padding: 0.95rem 1.4rem;
  border-radius: 10px;
  margin-top: 0.5rem;
  border: 2px solid ${gold};
  word-break: break-all;
  box-shadow: 0 2px 8px 0 rgba(110,75,35,0.12);
  @media (max-width: 600px) {
    font-size: 0.98rem;
    padding: 0.6rem 0.6rem;
  }
`;

const BuyButton = styled(motion.button)`
  padding: 1.15rem 2.8rem;
  font-size: 1.26rem;
  background: linear-gradient(90deg, ${gold} 0%, ${brown} 100%);
  border: 2.5px solid ${gold};
  border-radius: 22px;
  color: #fff;
  cursor: pointer;
  font-weight: 700;
  margin: 1.3rem 0 0.7rem 0;
  box-shadow: 0 2px 12px 0 rgba(110,75,35,0.13);
  text-shadow: 0 1px 0 ${yellow};
  letter-spacing: 1px;
  transition: transform 0.2s, box-shadow 0.2s, background 0.2s;
  font-family: 'Merriweather', serif;
  &:hover {
    transform: scale(1.07);
    box-shadow: 0 4px 24px 0 rgba(255, 215, 0, 0.25);
    background: linear-gradient(90deg, ${brown} 0%, ${gold} 100%);
    color: #f8ecd1;
  }
  @media (max-width: 600px) {
    font-size: 1.05rem;
    padding: 0.8rem 1.2rem;
  }
`;

const SocialButtons = styled.div`
  display: flex;
  gap: 1.5rem;
  margin: 1.2rem 0 0.2rem 0;
  justify-content: center;
  @media (max-width: 600px) {
    gap: 0.8rem;
    margin: 0.7rem 0 0.1rem 0;
  }
`;

const SocialButton = styled(motion.a)`
  font-size: 2.3rem;
  color: ${gold};
  background: linear-gradient(120deg, #2b2112 60%, #3e2a13 100%);
  border-radius: 50%;
  padding: 0.6rem;
  border: 2px solid ${gold};
  transition: color 0.2s, background 0.2s, box-shadow 0.2s;
  box-shadow: 0 1.5px 6px 0 rgba(110,75,35,0.13);
  &:hover {
    color: #1a1208;
    background: ${gold};
    box-shadow: 0 4px 16px 0 rgba(255, 215, 0, 0.13);
  }
  @media (max-width: 600px) {
    font-size: 1.5rem;
    padding: 0.32rem;
  }
`;

const Footer = styled(Card)`
  font-size: 1.18rem;
  color: ${gold};
  background: linear-gradient(90deg, #2b2112 80%, #3e2a13 100%);
  margin-bottom: 2rem;
  border: 2px solid ${gold};
  @media (max-width: 600px) {
    font-size: 0.97rem;
    margin-bottom: 1rem;
  }
`;

// Roadmap Section
const RoadmapSection = styled.section`
  width: 100vw;
  max-width: 100vw;
  margin: 0 auto 0.7rem auto;
  padding: 0.5rem 0 2.4rem 0;
  overflow-x: auto;
`;
const RoadmapTitle = styled.h2`
  font-family: 'Merriweather', serif;
  color: ${gold};
  font-size: 2rem;
  margin: 0 0 1.1rem 1.7vw;
  font-weight: 700;
  letter-spacing: 1px;
  text-shadow: 0 2px 0 #1a1208, 0 0 8px ${gold};
`;
const RoadmapScroller = styled.div`
  display: flex;
  flex-direction: row;
  gap: 1.7rem;
  overflow-x: auto;
  padding: 0.2rem 1vw 0.2rem 1.7vw;
  scroll-snap-type: x mandatory;
  @media (max-width: 600px) {
    gap: 1.1rem;
    padding: 0.2rem 0.5vw 0.2rem 0.7vw;
  }
`;
const RoadmapCard = styled.div`
  min-width: 320px;
  background: linear-gradient(135deg, #2b2112 85%, #3e2a13 100%);
  border: 2px solid ${gold};
  border-radius: 18px;
  box-shadow: 0 4px 16px 0 rgba(26, 18, 8, 0.17);
  color: #f8ecd1;
  font-family: 'Merriweather', serif;
  font-size: 1.1rem;
  padding: 1.5rem 1.3rem;
  scroll-snap-align: start;
  display: flex;
  flex-direction: column;
  gap: 0.7rem;
  @media (max-width: 600px) {
    min-width: 220px;
    font-size: 0.98rem;
    padding: 1rem 0.6rem;
  }
`;

function App() {
  return (
    <>
      <Navbar>
        <NavbarBrand>Jiggit</NavbarBrand>
        <NavbarLinks>
          <NavbarLink href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer"><FaTelegram />Telegram</NavbarLink>
          <NavbarLink href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer"><FaXTwitter />Twitter</NavbarLink>
          <NavbarLink href="https://medium.com/@jiggit" target="_blank" rel="noopener noreferrer"><FaMedium />Medium</NavbarLink>
        </NavbarLinks>
      </Navbar>
      <AppContainer>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <MascotCircle
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ type: 'spring', stiffness: 180, damping: 18 }}
            >
              <MascotImg src="/assets/mascot.png" alt="Jiggit Mascot" />
            </MascotCircle>
            <Title>$JIGGIT</Title>
            <Description>
              <b>Jiggit — The Spirit of Central Asia on the Blockchain</b>
              <br /><br />
              Jiggit is not just a cryptocurrency — it’s the heartbeat of Central Asia, reimagined for the digital age.<br /><br />
              <span style={{ display: 'block', marginBottom: '0.7em' }}>
                Our mission is to revive global interest in the rich heritage, landscapes, and spirit of Central Asia, while building a new economy for tourism, culture, and adventure.
              </span>
              With Jiggit, travelers can access unique experiences, earn rewards for exploration, and support local businesses and communities directly and transparently.<br /><br />
              Inspired by the legendary <b>"jigits"</b> — the fearless, agile horsemen who symbolized honor and freedom — Jiggit brings that same spirit into the future: <b>bold, unstoppable, authentic</b>.
            </Description>
            <QualitiesList>
              <Quality>brave</Quality>
              <Quality>agile</Quality>
              <Quality>courageous</Quality>
              <Quality>honest</Quality>
              <Quality>fair</Quality>
              <Quality>able not only to fight but also to protect the honor of his family or people.</Quality>
            </QualitiesList>
          </Card>
          <Footer
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.1, delay: 0.25 }}
          >
            Join to our Jiggit nomad community
          </Footer>
        </CardColumn>
        <CardColumn>
          <Card
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.9, delay: 0.1 }}
          >
            <Title>Token Address</Title>
            <AddressText>0x123...789</AddressText>
            <BuyButton
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.98 }}
            >
              Buy $JIGGIT
            </BuyButton>
          </Card>
        </CardColumn>
      </AppContainer>
      <RoadmapSection>
        <RoadmapTitle>Roadmap</RoadmapTitle>
        <RoadmapScroller>
          <RoadmapCard>
            <b>Q2 2025: Launch & Community</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Token launch & DEX listing</li>
              <li>Community building</li>
              <li>Initial partnerships with local businesses</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q3 2025: Ecosystem Growth</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Traveler rewards system</li>
              <li>Tourism experience marketplace</li>
              <li>Mobile app beta</li>
            </ul>
          </RoadmapCard>
          <RoadmapCard>
            <b>Q4 2025: Global Expansion</b>
            <ul style={{margin:'0.5em 0 0 1.2em',padding:0}}>
              <li>Major exchange listings</li>
              <li>International marketing</li>
              <li>Full launch of Jiggit platform</li>
            </ul>
          </RoadmapCard>
        </RoadmapScroller>
      </RoadmapSection>
    </>
  );
}

export default App;
