import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaTelegram, FaMedium } from 'react-icons/fa';
import { FaXTwitter } from 'react-icons/fa6';
import AnimatedBg from './components/AnimatedBg';
import HeroSection from './components/HeroSection';
import Roadmap from './components/RoadmapSection';

// Refined pixel palette for dark orange, yellow, brown
const darkOrange = '#c97d3d';
const richBrown = '#5a3b1b';
const goldYellow = '#f7c873';
const deepYellow = '#e8b03b';
const lightBrown = '#a67c52';
const pixelDark = '#23190f';
const pixelLight = '#f7eee1';

// Assign main colors
const background = pixelDark;
const primary = darkOrange;
const secondary = goldYellow;
const accent = deepYellow;
const cardBg = pixelLight;
const cardBorder = richBrown;
const textMain = richBrown;
const textSubtle = lightBrown;

// Pixel font
const pixelFont = `'Press Start 2P', 'Spectral SC', monospace`;

// Navbar - horizontal, pixel art style
const Navbar = styled.nav`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 1.3rem 2.7vw 1.3rem 1.7vw;
  box-sizing: border-box;
  border-bottom: 4px solid ${accent};
  position: sticky;
  top: 0;
  z-index: 10;
  font-family: ${pixelFont};
  image-rendering: pixelated;
`;

const NavbarBrand = styled.div`
  font-family: ${pixelFont};
  font-size: 2.1rem;
  color: ${primary};
  font-weight: 900;
  text-shadow: 2px 2px 0 ${accent}, 0 0 8px ${primary};
  letter-spacing: 3px;
  cursor: pointer;
  text-transform: uppercase;
  margin-bottom: 0;
  image-rendering: pixelated;
  @media (max-width: 700px) {
    font-size: 1.3rem;
  }
`;

const NavbarLinks = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2.1rem;
  align-items: center;
  @media (max-width: 700px) {
    flex-direction: column; 
    gap: 1rem; 
  }
`;
const NavbarLink = styled.a`
  color: ${accent};
  font-size: 0.9rem;
  text-decoration: none;
  padding: 0.62rem 2.1rem;
  border-radius: 9px;
  background: ${richBrown};
  border: 3px solid ${accent};
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background 0.2s, color 0.2s, box-shadow 0.2s;
  font-family: ${pixelFont};
  letter-spacing: 0.04em;
  image-rendering: pixelated;
  box-shadow: 3px 3px 0 ${secondary};
  &:hover {
    background: ${accent};
    color: ${richBrown};
    box-shadow: 6px 6px 0 ${primary};
  }
  @media (max-width: 700px) {
    font-size: 1rem; /* Увеличено с 0.9rem для читаемости */
    padding: 0.8rem 1.5rem; /* Увеличено для сенсорных зон */
    width: 100%; /* Полная ширина для удобства нажатия */
    justify-content: center; /* Центрирование содержимого */
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  color: ${textMain};
  padding: 0.2rem 1.5rem 2rem 1.5rem;
  position: relative;
  font-family: ${pixelFont};
  background: transparent;
  gap: 2.5rem;
  z-index: 1;
  image-rendering: pixelated;
  @media (max-width: 900px) {
    gap: 1.5rem;
    padding: 1.5rem; /* Увеличено с 1.2rem 0.4rem */
  }
  @media (max-width: 700px) {
    gap: 1rem; /* Увеличено с 0.7rem */
    padding: 1rem; /* Увеличено с 0.6rem 0.2rem для отступов */
  }
`;

const MascotBottom = styled.img`
  display: block;
  margin: 2rem auto 0 auto;
  width: 210px;
  height: auto;
  image-rendering: pixelated;
  filter: drop-shadow(0 8px 32px #c97d3d44);
  @media (max-width: 700px) {
    width: 120px;
    margin-top: 1.2rem;
  }
`;

const BottomSection = styled.section`
  width: 100vw;
  background: ${primary};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding: 0 0 2.5rem 0;
  margin-top: 0;
  border-radius: 0 0 1.5rem 1.5rem;
  min-height: 320px;
  position: relative;
  z-index: 11;
  overflow: hidden;
  @media (max-width: 700px) {
    border-radius: 0 0 0.7rem 0.7rem;
    min-height: 180px;
  }
`;

const BottomBuyButton = styled.a`
  display: block;
  margin: 2.2rem auto 0 auto;
  background: #f7eee1;
  color: ${primary};
  font-family: ${pixelFont};
  font-size: 2rem;
  font-weight: bold;
  border: 4px solid ${richBrown};
  border-radius: 2.5rem;
  padding: 1.2rem 3.5rem;
  box-shadow: 0 4px 0 0 ${richBrown};
  text-align: center;
  text-decoration: none;
  transition: background 0.18s, color 0.18s, border 0.18s, box-shadow 0.18s;
  cursor: pointer;
  &:hover {
    background: ${accent};
    color: #fff8e1;
    border: 4px solid ${accent};
    box-shadow: 0 8px 24px ${accent}66;
  }
  @media (max-width: 700px) {
    font-size: 1.1rem;
    padding: 0.7rem 1.5rem;
  }
`;

const BottomSubtext = styled.div`
  color: ${richBrown};
  font-family: ${pixelFont};
  font-size: 1rem;
  margin: 1.5rem auto 0 auto;
  opacity: 0.7;
  text-align: center;
  @media (max-width: 700px) {
    font-size: 0.8rem;
    margin: 0.8rem auto 0 auto;
  }
`;

const BottomMascot = styled.img`
  display: block;
  margin: 2.5rem auto 0 auto;
  width: 210px;
  height: auto;
  image-rendering: pixelated;
  filter: drop-shadow(0 8px 32px #c97d3d44);
  border-radius: 1.2rem 1.2rem 2.5rem 2.5rem;
  @media (max-width: 700px) {
    width: 120px;
    margin-top: 1.2rem;
  }
`;

function App() {
  return (
    <>
      <AnimatedBg />
      <Navbar>
        <NavbarBrand>JIGGIT</NavbarBrand>
        <NavbarLinks>
          <NavbarLink href="https://t.me/jiggit" target="_blank" rel="noopener noreferrer"><FaTelegram />Telegram</NavbarLink>
          <NavbarLink href="https://twitter.com/jiggit" target="_blank" rel="noopener noreferrer"><FaXTwitter />Twitter</NavbarLink>
          <NavbarLink href="https://medium.com/@zerobladeamir" target="_blank" rel="noopener noreferrer"><FaMedium />Medium</NavbarLink>
        </NavbarLinks>
      </Navbar>
      <AppContainer>
        <HeroSection />
        <Roadmap />
      </AppContainer>
      <BottomSection>
        <BottomBuyButton href="#buy" target="_blank" rel="noopener noreferrer">Buy $Jiggit</BottomBuyButton>
        <BottomSubtext>→ Powered by Jiggit token team</BottomSubtext>
        <BottomMascot src="/assets/mascot_bottom.png" alt="Jiggit Mascot" />
      </BottomSection>
    </>
  );
}

export default App;
